package methods2;

public class Arithrmatic {
	
	public void add()
	{
		int a=23;
		int b=22;
		int sum=a+b;
		System.out.println(sum);
	}
	public void sub()
	{
		int a=88;
		int b=65;
		int sub=a-b;
		System.out.println(sub);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Arithrmatic a=new Arithrmatic();
		a.add();
		a.sub();
		
	}

}
